package redes_digitais_controle_de_notas.persistence.entity;

import java.util.*;
import javax.inject.Inject;
import javax.persistence.*;

import br.gov.frameworkdemoiselle.stereotype.PersistenceController;

import redes_digitais_controle_de_notas.domain.entity.Turma;
import br.gov.frameworkdemoiselle.template.JPACrud;
import javax.persistence.criteria.*;

@PersistenceController
public class TurmaDAO extends JPACrud {

	private static final long serialVersionUID = 1L;

	@Override
	public List findByExample(Turma example) {
		if (example == null) {
			example = new Turma();
		}
		return super.findByExample(example);
	}

	public List findByCriteria(java.util.Map parameters) {
		if (parameters == null) {
			parameters = new java.util.HashMap();
		}
		CriteriaBuilder cb = super.getCriteriaBuilder();
		CriteriaQuery q = cb.createQuery(Turma.class);
		Root r = q.from(Turma.class);
		Predicate where = null;
		Object parameter = null; 
		parameter = parameters.get("idTurma");
		if (parameter != null) {
			Predicate expression = null;
			if (parameter instanceof java.util.List) {
				@SuppressWarnings("unchecked")
				List collection = (List)parameter;
				expression = r.get("nomeTurma").in(collection);
			} else if (parameter instanceof String && ((String)parameter).length()>0) {
				expression = cb.like(r.get("nomeTurma"), "%" + parameter + "%");
			}
			if (expression != null) {
				if (where == null) {
					where = expression;
				} else {
					where = cb.and(where, expression);
				}
			}
		}
		List results = new ArrayList();
		if (where != null) {
			q.where(where);
			results =  getEntityManager().createQuery(q).getResultList();
		}
		return results;
	}

}

